import { Button, View, Text, Image } from "react-native";

export default function PetersonScreen({ navigation }) {
  return (
    <View style={{ flex: 1 }}>
      <View style={{ flex: 1, alignItems: "center" }}>
        <Text
          style={{
            textAlign: "center",
            color: "black",
            fontSize: 20,
            fontWeight: "bold",
            width: 340,
            backgroundColor: "white",
          }}
        >
          L'algoritmo di Peterson o algoritmo tie-breaker è un algoritmo
          sviluppato nella teoria del controllo della concorrenza per coordinare
          due o più processi o thread che hanno una sezione critica in cui deve
          esservi mutua esclusione. L'algoritmo assicura la corretta
          sincronizzazione, impedendo lo stallo (deadlock) ed assicurando che
          soltanto un processo alla volta possa eseguire una sezione critica
          (serializzazione). A causa del modo in cui i moderni elaboratori
          eseguono le istruzioni elementari del linguaggio macchina come load e
          store, non è affatto certo che l'algoritmo di Peterson funzioni
          correttamente su tali sistemi.
        </Text>
        <Image
          source={require("../images/Puzzle.jpg")}
          style={{
            position: "absolute",
            zIndex: -1,
            width: "100%",
            height: "100%",
          }}
        />
      </View>
    </View>
  );
}
